    private static int checkForXMAS(int col, int row, List<String> input) {
        int count = 0;
        // check up
        if(row + 3 < input.size()) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row + 1).charAt(col) + input.get(row + 2).charAt(col) + input.get(row + 3).charAt(col);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check up right
        if(row + 3 < input.size() && col + 3 < input.get(row).length()) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row + 1).charAt(col + 1) + input.get(row + 2).charAt(col + 2) + input.get(row + 3).charAt(col + 3);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check right
        if(col + 3 < input.get(row).length()) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row).charAt(col + 1) + input.get(row).charAt(col + 2) + input.get(row).charAt(col + 3);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check down right
        if(row - 3 >= 0 && col + 3 < input.get(row).length()) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row - 1).charAt(col + 1) + input.get(row - 2).charAt(col + 2) + input.get(row - 3).charAt(col + 3);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check down
        if(row - 3 >= 0) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row - 1).charAt(col) + input.get(row - 2).charAt(col) + input.get(row - 3).charAt(col);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check down left
        if(row - 3 >= 0 && col - 3 >= 0) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row - 1).charAt(col - 1) + input.get(row - 2).charAt(col - 2) + input.get(row - 3).charAt(col - 3);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check left
        if(col - 3 >= 0) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row).charAt(col - 1) + input.get(row).charAt(col - 2) + input.get(row).charAt(col - 3);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        // check down left
        if(row + 3 < input.size() && col - 3 >= 0) {
            String xmas = "" + input.get(row).charAt(col) + input.get(row + 1).charAt(col - 1) + input.get(row + 2).charAt(col - 2) + input.get(row + 3).charAt(col - 3);
            if (xmas.equals("XMAS")) {
                count++;
            }
        }
        return count;
    }

