package main

import (
	"fmt"
	"strconv"
	"strings"
)

func main() {
	reports := readInput(PUZZLE_INPUT)
	ocurrences := findXMasOccurrences(reports)
	println("------------------------------------------------")
	println("Number of X-MAS occurrences: " + strconv.Itoa(ocurrences))
	println("------------------------------------------------")
}

func readInput(input string) []string {
	return strings.Split(input, "\n")[1:]
}

func findXMasOccurrences(lines []string) int {
	occurences := 0

	all := append(lines, getColumns(lines)...)
	all = append(all, getCrosses(lines)...)

	for _, line := range all {
		occurences += strings.Count(line, "XMAS")
		occurences += strings.Count(line, "SAMX")
	}

	return occurences
}

func getColumns(lines []string) []string {
	columns := make([]string, len(lines[0]))
	for _, line := range lines {
		for i, char := range line {
			columns[i] += string(char)
		}
	}
	return columns
}

func getCrosses(lines []string) []string {
	var crossesLeftToRight []string
	var crossesRightToLeft []string
	for _, char := range lines[0] {
		crossesLeftToRight = append(crossesLeftToRight, string(char))
		crossesRightToLeft = append(crossesRightToLeft, string(char))
	}
	fmt.Println(crossesRightToLeft)
	for j, line := range lines[1:] {
		for i, char := range line {
			if i == 0 {
				crossesLeftToRight = append([]string{string(char)}, crossesLeftToRight...)
			} else {
				crossesLeftToRight[i] += string(char)
			}

			if i == len(line)-1 {
				crossesRightToLeft = append(crossesRightToLeft, string(char))
			} else {
				crossesRightToLeft[j+i+1] += string(char)
			}
		}
	}
	return append(crossesLeftToRight, crossesRightToLeft...)
}

